# Langkah 1: Buat daftar semua (teks, label) pasangan
all_unique_texts_with_labels = []
for label, texts in base_text.items():
    for text_content in texts:
        all_unique_texts_with_labels.append((text_content, label))

# Langkah 2: Acak daftar tersebut
random.shuffle(all_unique_texts_with_labels)

# Langkah 3: Tentukan jumlah baris yang akan dibuat
total_available_unique_texts = len(all_unique_texts_with_labels)
actual_num_rows = min(num_rows, total_available_unique_texts)

if num_rows > total_available_unique_texts:
    print(f"Peringatan: num_rows ({num_rows}) lebih besar dari jumlah teks unik yang tersedia ({total_available_unique_texts}).")
    print(f"Dataset akan dibuat dengan {total_available_unique_texts} baris untuk menjaga keunikan konten dasar.")
elif num_rows == 0:
    print("Peringatan: num_rows adalah 0. Tidak ada data yang akan dibuat.")
    actual_num_rows = 0


# Bangun dataset
data = {
    "id": [],
    "note_content": [],
    "label": []
}

start_time_script = datetime.datetime.now()
if actual_num_rows > 0:
    print(f"Memulai pembuatan dataset dengan {actual_num_rows} baris unik...")
    print(f"Timestamp mulai: {start_time_script.strftime('%Y-%m-%d %H:%M:%S')}")

    # Langkah 4: Isi data
    for i in range(actual_num_rows):
        base_content, chosen_label = all_unique_texts_with_labels[i]
        # Tambahkan UUID untuk keunikan absolut baris jika masih diinginkan,
        # meskipun konten dasarnya sudah unik dalam dataset ini.
        note = f"{base_content} #{uuid.uuid4()}"

        data["id"].append(i + 1)
        data["note_content"].append(note)
        data["label"].append(chosen_label)

        if (i + 1) % (actual_num_rows // 20 if actual_num_rows > 20 else 1) == 0 or (i + 1) == actual_num_rows:
            current_time = datetime.datetime.now()
            elapsed_time = current_time - start_time_script
            print(f"Telah memproses {i + 1} baris dari {actual_num_rows}... ({current_time.strftime('%Y-%m-%d %H:%M:%S')}, Durasi: {elapsed_time})")
else:
    print("Tidak ada baris yang diproses karena actual_num_rows adalah 0.")


